        [DataObjectMethod(DataObjectMethodType.Select)]
        internal GaikanKensaKekkaTblDataSet.KensaKekkaInputShosaiDataTable GetDataByKensaIraiKey(string kensaIraiHoteiKbn, string kensaIraiHokenjoCd, string kensaIraiNendo, string kensaIraiRenban)
        {
            SqlCommand command = CreateSqlCommand(kensaIraiHoteiKbn, kensaIraiHokenjoCd, kensaIraiNendo, kensaIraiRenban);

            SqlDataAdapter adpt = new SqlDataAdapter(command);
            adpt.SelectCommand.Connection = this.Connection;

            GaikanKensaKekkaTblDataSet.KensaKekkaInputShosaiDataTable dataTable = new GaikanKensaKekkaTblDataSet.KensaKekkaInputShosaiDataTable();
            adpt.Fill(dataTable);

            return dataTable;
        }
